package name.tachenov.leetcode;

import static org.assertj.core.api.Assertions.*;
import org.testng.annotations.*;

public class LongestSubstringWithAtLeastKRepeatingCharactersTest {
    
    @Test(dataProvider = "strings")
    public void testSomeMethod(String s, int k, int expected) {
        LongestSubstringWithAtLeastKRepeatingCharacters lswalkrc
                = new LongestSubstringWithAtLeastKRepeatingCharacters();
        assertThat(lswalkrc.longestSubstring(s, k)).isEqualTo(expected);
    }
    
    @DataProvider
    public Object[][] strings() {
        return new Object[][] {
            {"aaa", 3, 3},
            {"aaa", 4, 0},
            {"aaabb", 3, 3},
            {"ababbc", 2, 5},
            {"baaaa", 3, 4},
            {"aaaab", 3, 4},
            {"baaab", 3, 3},
            {"abxyxyxyuw", 3, 6},
            {"axyxyxyuw", 3, 6},
            {"xyxyxyuw", 3, 6},
            {"abxyxyxyu", 3, 6},
            {"",
                100000, 0},
        };
    }
    
}
